(function(){
	'use strict';
	window.addEventListener('load', function(){
		var isMI = window.parent.hasOwnProperty('context'),
			css, head, style;

		if(isMI && !window.parent.document.getElementById('memoryWarningStyle')){
			css = '.memoryWarning { display: none!important; }';
			head = window.parent.document.getElementsByTagName('head')[0];
			style = document.createElement('style');

			style.type = 'text/css';
			style.id = 'memoryWarningStyle';
			if(style.styleSheet){
				style.styleSheet.cssText = css;
			}else{
				style.appendChild(document.createTextNode(css));
			}

			head.appendChild(style);
		}
	});
})();